def on_hit_wall(sprite, location):
    global jump
    if not (playerGreen.is_hitting_tile(CollisionDirection.TOP)):
        jump = 0
    if playerGreen.is_hitting_tile(CollisionDirection.LEFT) or playerGreen.is_hitting_tile(CollisionDirection.RIGHT):
        playerGreen.vy = 0
scene.on_hit_wall(SpriteKind.player, on_hit_wall)

