def lavaLevel(num2: number):
    global lava
    if num2 <= 3:
        lava += 1
        for index4 in range(31):
            tiles.set_tile_at(tiles.get_tile_location(index4, lava * -1 + 134),
                sprites.dungeon.hazard_lava1)
            tiles.set_wall_at(tiles.get_tile_location(index4, lava * -1 + 134), False)
    else:
        if num2 <= 7:
            lava += 5
            for index5 in range(31):
                tiles.set_tile_at(tiles.get_tile_location(index5, lava * -1 + 134),
                    sprites.dungeon.hazard_lava1)
                tiles.set_wall_at(tiles.get_tile_location(index5, lava * -1 + 134), False)
        else:
            lava += 10
            for index6 in range(31):
                tiles.set_tile_at(tiles.get_tile_location(index6, lava * -1 + 134),
                    sprites.dungeon.hazard_lava1)
                tiles.set_wall_at(tiles.get_tile_location(index6, lava * -1 + 134), False)

